mapper.update:
UPDATE T_UDMP_BATCH_AGENT
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() ,
WHERE AGENT_ID = #{agent_id}

UPDATE T_UDMP_BATCH_AGENT
AGENT_NAME = #{agent_name, jdbcType=VARCHAR} , AGENT_IP = #{agent_ip, jdbcType=VARCHAR} , AGENT_SYSTEM = #{agent_system, jdbcType=VARCHAR} , AGENT_THREAD_LIMIT_CNT = #{agent_thread_limit_cnt, jdbcType=NUMERIC} , AGENT_PORT = #{agent_port, jdbcType=NUMERIC} , IS_ENABLE = #{is_enable, jdbcType=VARCHAR} , IS_DELETED = #{is_deleted, jdbcType=VARCHAR} , UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP()
WHERE AGENT_ID= #{agent_id}
