mapper.update:
UPDATE T_UDMP_BATCH_JOB_PARAM
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() ,
WHERE JOB_ID = #{job_id} AND IS_DELETED = #{is_deleted}

UPDATE T_UDMP_BATCH_JOB_PARAM
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() ,
WHERE JOB_ID = #{job_id}

UPDATE T_UDMP_BATCH_JOB_PARAM
TASK_PARAM_ID = #{task_param_id, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , ARRAY_PARAM_ORDER = #{array_param_order, jdbcType=NUMERIC} , JOB_ID = #{job_id, jdbcType=NUMERIC} , IS_DELETED = #{is_deleted, jdbcType=VARCHAR} , UPDATE_BY = #{update_by, jdbcType=NUMERIC} , VER = #{ver, jdbcType=VARCHAR} , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() , PARAM_VALUE = #{param_value, jdbcType=VARCHAR} ,
WHERE JOB_PARAM_ID = #{job_param_id}

UPDATE T_UDMP_BATCH_JOB_PARAM



          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.task_param_id} 




          	WHEN JOB_PARAM_ID=#{item.job_param_id} THEN SYSDATE()




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.array_param_order} 




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.job_id} 




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.is_deleted} 




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.update_by} 




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.ver} 




          	 WHEN JOB_PARAM_ID=#{item.job_param_id} THEN #{item.param_value} 




          	WHEN JOB_PARAM_ID=#{item.job_param_id} THEN CURRENT_TIMESTAMP()



 WHERE
  JOB_PARAM_ID=#{item.job_param_id}
