mapper.update:
UPDATE T_UDMP_BATCH_JOB_RUN_LOG
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() ,
WHERE JOB_RUN_LOG_ID = #{job_run_log_id}

UPDATE T_UDMP_BATCH_JOB_RUN_LOG
JOB_RUN_LOG_ID = #{job_run_log_id, jdbcType=NUMERIC} , LOG_TIME = #{log_time, jdbcType=DATE} , LOG_INFO = #{log_info, jdbcType=VARCHAR} , LOG_LEVEL = #{log_level, jdbcType=VARCHAR} , LOG_TYPE = #{log_type, jdbcType=VARCHAR} , JOB_RUN_ID = #{job_run_id, jdbcType=NUMERIC} ,
WHERE JOB_RUN_LOG_ID = #{job_run_log_id}
