mapper.update:
UPDATE T_UDMP_BATCH_TASK
IS_DELETED = 1, UPDATE_TIME = SYSDATE() , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() ,
WHERE TASK_ID = #{task_id}

UPDATE T_UDMP_BATCH_TASK
UPDATE_TIME = SYSDATE() , IS_DELETED = #{is_deleted, jdbcType=VARCHAR} , UPDATE_BY = #{update_by, jdbcType=NUMERIC} , TASK_NAME = #{task_name, jdbcType=VARCHAR} , IS_SPRING_BEAN = #{is_spring_bean, jdbcType=VARCHAR} , VER = #{ver, jdbcType=VARCHAR} , TASK_SYSTEM = #{task_system, jdbcType=VARCHAR} , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP() , TASK_CLAZZ = #{task_clazz, jdbcType=VARCHAR} ,
WHERE TASK_ID = #{task_id}
