INSERT INTO T_UDMP_BATCH_TASK_PARAM(
	TASK_PARAM_ID, INSERT_TIME, UPDATE_TIME, TASK_ID, PARAM_ORDER, IS_ARRAY, IS_DELETED, 
	INSERT_TIMESTAMP, TASK_PARAM_NAME,TASK_PARAM_CODE, UPDATE_BY, TASK_PARAM_DATA_TYPE, VER, UPDATE_TIMESTAMP, PARAM_ID, 
	IS_MANU, INSERT_BY, IS_REQUIRED ) 
SELECT S_T_UDMP_BATCH_TASK_PARAM.NEXTVAL, A.* 
FROM(	

SELECT
	SYSDATE AS INSERT_IIME, SYSDATE AS UPDATE_TIME, #{item.task_id, jdbcType=NUMERIC} , #{item.param_order, jdbcType=NUMERIC} , #{item.is_array, jdbcType=VARCHAR} , #{item.is_deleted, jdbcType=VARCHAR} 
	, CURRENT_TIMESTAMP AS INSERT_TIMESTAMP, #{item.task_param_name, jdbcType=VARCHAR} ,#{item.task_param_code, jdbcType=VARCHAR} , #{item.update_by, jdbcType=NUMERIC} , #{item.task_param_data_type, jdbcType=VARCHAR} , #{item.ver, jdbcType=VARCHAR} , CURRENT_TIMESTAMP AS UPDATE_TIMESTAMP, #{item.param_id, jdbcType=NUMERIC} 
	, #{item.is_manu, jdbcType=VARCHAR} , #{item.insert_by, jdbcType=NUMERIC} , #{item.is_required, jdbcType=VARCHAR}
FROM DUAL

) A

mapper.update:
UPDATE T_UDMP_BATCH_TASK_PARAM SET IS_DELETED = 1, UPDATE_TIME = SYSDATE , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP WHERE TASK_ID IN (
#{item.task_id, jdbcType=NUMERIC}
)

UPDATE T_UDMP_BATCH_TASK_PARAM
