mapper.update:
UPDATE T_UDMP_BATCH_TEST_DEST
UPDATE_BY = #{update_by, jdbcType=NUMERIC} , CLOB_D = #{clob_d, jdbcType=DATE} , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP , UPDATE_TIME = SYSDATE , CHAR_B = #{char_b, jdbcType=VARCHAR} , VCHAR_C = #{vchar_c, jdbcType=VARCHAR} ,
WHERE NUM_A = #{num_a}

mapper.select:
 SELECT * FROM ( SELECT rownum as rn, A.NUM_A, A.CLOB_D, 
	A.CHAR_B, A.VCHAR_C FROM T_UDMP_BATCH_TEST_DEST A WHERE 1=1 

 ORDER BY A.NUM_A )m where m.rn>=#{start_num} and m.rn<#{end_num}
