mapper.update:
UPDATE T_UDMP_BATCH_PARAM
IS_DELETED = 1, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP ,
WHERE Param_ID = #{param_id}

UPDATE T_UDMP_BATCH_PARAM
Param_TYPE = #{param_type, jdbcType=VARCHAR} , PARAM_DATA_TYPE = #{param_data_type, jdbcType=VARCHAR} , IS_SINGLE_VALUE = #{is_single_value, jdbcType=VARCHAR} , PARAM_CODE = #{param_code, jdbcType=VARCHAR} , PARAM_NAME = #{param_name, jdbcType=VARCHAR} , PARAM_DESC = #{param_desc, jdbcType=VARCHAR} , PARAM_VALUE = #{param_value, jdbcType=VARCHAR} , IS_DELETED = #{is_deleted, jdbcType=VARCHAR}, UPDATE_BY = #{update_by, jdbcType=NUMERIC} , UPDATE_TIME = SYSDATE , UPDATE_TIMESTAMP = CURRENT_TIMESTAMP ,
WHERE Param_ID = #{param_id}
